@import url("https://fonts.googleapis.com/css?family=Montserrat:400,700");
:root {
  --font-stack: normal 1rem/1.5 'Montserrat', sans-serif;
  --font-title: bold 2rem/2 'Montserrat', sans-serif;
  --scroll-indicator-height: .25em;
  --color: #3d50b4;
  --color-shadow: #14a092; }

/*  */
*, *::after, *::before {
  box-sizing: border-box; }

html {
  /* Sets standard font */
  font: var(--font-stack);
  /* Sets standard font-size */
  font-size: 1rem; }

html, body {
  margin: 0;
  padding: 0; }

body {
  margin: 0;
  padding: 0; }

main {
  margin-top: 5rem; }

nav {
  width: 100%;
  height: 5rem;
  position: fixed;
  top: 0;
  left: 0;
  background-color: #FFF; }
  nav ul {
    display: flex;
    align-items: center;
    width: 100%;
    height: 100%;
    padding: 0;
    margin: 0; }
    nav ul li {
      list-style: none;
      margin: 0 1rem; }
      nav ul li a {
        text-decoration: none;
        color: var(--color); }
        nav ul li a::after {
          content: '';
          display: block;
          width: 100%;
          height: .2em;
          position: relative;
          bottom: 0;
          background-color: var(--color);
          transform-origin: 20% 0;
          transform: scaleX(0);
          background-color: #1bd3c0;
          transition: transform 150ms ease-out, background-color 300ms ease-out; }
        nav ul li a:hover::after {
          background-color: var(--color);
          transform: scaleX(1); }
      nav ul li [aria-current="page"] {
        border-bottom: 0.2em solid var(--color); }

[data-section] {
  overflow-y: scroll;
  min-height: 100vh;
  height: auto;
  width: 100%;
  padding: 2rem;
  background-color: #3d50b4;
  transform: scale(0);
  opacity: 0;
  transform-origin: 50% 50%;
  will-change: auto; }
  [data-section] h1 {
    font-size: 4rem;
    color: #fff; }
  [data-section] p {
    color: #fff; }
  [data-section] [data-pokemon] {
    cursor: pointer;
    color: #FFF;
    text-transform: capitalize; }
    [data-section] [data-pokemon] article {
      display: flex;
      padding-left: 1em;
      border: 1px solid white; }
      [data-section] [data-pokemon] article figure {
        margin: 0 0 0 2rem; }
        [data-section] [data-pokemon] article figure img {
          transform: scale(1.5);
          width: 8em;
          height: auto; }
